def sort_and_compare(list1, list2, list3):
    sorted_list1 = sorted(list1)
    sorted_list2 = sorted(list2)
    sorted_list3 = sorted(list3)

    are_equal = (sorted_list1 == sorted_list2) and (sorted_list2 == sorted_list3)
    return sorted_list1, sorted_list2, sorted_list3, are_equal

